mapper.select:
select 

from organization_executive_power
where organization_executive_power_id = #{organizationExecutivePowerId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}

mapper.delete:
delete from organization_executive_power
where organization_executive_power_id = #{organizationExecutivePowerId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}

mapper.update:
update organization_executive_power
set house_visiable = #{houseVisiable,jdbcType=BIT},
  custom_visiable = #{customVisiable,jdbcType=BIT},
  public_disk_host_visiable = #{publicDiskHostVisiable,jdbcType=BIT},
  private_disk_host_visiable = #{privateDiskHostVisiable,jdbcType=BIT},
  house_followup_visibale = #{houseFollowupVisibale,jdbcType=BIT}
where organization_executive_power_id = #{organizationExecutivePowerId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}
