mapper.select:
select 

from organization_executive_region
where organization_executive_region_id = #{organizationExecutiveRegionId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}

mapper.delete:
delete from organization_executive_region
where organization_executive_region_id = #{organizationExecutiveRegionId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}

mapper.insert:
insert into organization_executive_region (organization_executive_region_id, organization_id, 
  trading_area_id, house_visiable, custom_visiable
  )
values (#{organizationExecutiveRegionId,jdbcType=VARCHAR}, #{organizationId,jdbcType=VARCHAR}, 
  #{tradingAreaId,jdbcType=INTEGER}, #{houseVisiable,jdbcType=BIT}, #{customVisiable,jdbcType=BIT}
  )

mapper.update:
update organization_executive_region


    trading_area_id = #{tradingAreaId,jdbcType=INTEGER},


    house_visiable = #{houseVisiable,jdbcType=BIT},


    custom_visiable = #{customVisiable,jdbcType=BIT},


where organization_executive_region_id = #{organizationExecutiveRegionId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}

update organization_executive_region
set trading_area_id = #{tradingAreaId,jdbcType=INTEGER},
  house_visiable = #{houseVisiable,jdbcType=BIT},
  custom_visiable = #{customVisiable,jdbcType=BIT}
where organization_executive_region_id = #{organizationExecutiveRegionId,jdbcType=VARCHAR}
  and organization_id = #{organizationId,jdbcType=VARCHAR}
